import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function ServicesList() {
    const [services, setServices] = useState([]);

    useEffect(() => {
        const fetchServices = async () => {
            try {
                const response = await axios.get('/api/services');
                setServices(response.data);
            } catch (error) {
                console.error('Error fetching services:', error);
            }
        };
        fetchServices();
    }, []);

    return (
        <div className="services-list">
            {services.map(service => (
                <div key={service.id} className="service-item" style={{ width: '100%' }}>
                    <img src="/path/to/default/image.jpg" alt={service.name} style={{ width: '100%', height: '300px' }} />
                    <div style={{ padding: '20px' }}>
                        <h3>{service.name}</h3>
                        <p>Cena: Kƒç{service.price}</p>
                        <Link to={`/services/${service.id}`} className="link">View Details</Link>
                    </div>
                </div>
            ))}
        </div>
    );
}

export default ServicesList;
