import React, { useState, useEffect, useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Navbar.css'; // Ensure CSS path is correct

const Navbar = () => {
    const [name, setName] = useState('Guest');
    const { isLoggedIn, logout } = useContext(AuthContext);
    const navigate = useNavigate();

    useEffect(() => {
        // Fetch user name logic here if applicable
        const username = localStorage.getItem('username'); // Assuming username is stored
        if (username) {
            setName(username);
        }
    }, [isLoggedIn]);

    const handleLogout = () => {
        logout();
        navigate('/'); // Redirect using react-router
    };

    return (
        <div className='navbar'>
            <h1 onClick={() => navigate('/')} className='navbar-logo'>
                BarberShop
            </h1>
            <div className='nav-menu'>
                <ul className='navbar-ul'>
                    <li className='nav-items'><Link to="/" className='links'>HOME</Link></li>
                    <li className='nav-items'><Link to="/services" className='links'>SLUŽBY</Link></li>
                    <li className='nav-items'><Link to="/contact" className='links'>KONTAKT</Link></li>
                    <li className='nav-items'><Link to="/profile" className='links'>PROFIL</Link></li>
                </ul>
                {isLoggedIn ? (
                    <div className='user-loggedIn'>
                        <p>Hello, {name}!</p>
                        <button onClick={handleLogout} className="logout-button">Logout</button>
                    </div>
                ) : (
                    <div className='login-reg-links'>
                        <Link to='/login' className='links'>Login</Link>
                        <span> </span>
                        <Link to='/register' className='links'>Register</Link>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Navbar;
