import React, { useState } from 'react';
import './BookingForm.css'; // Make sure to create and link the CSS file

function BookingForm() {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        phone: '',
        reservationDate: '',
        time: ''
    });

    const handleChange = (event) => {
        setFormData({ ...formData, [event.target.name]: event.target.value });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        // Process form data here, you might want to integrate an API call
        console.log('Submitting reservation', formData);
        // Navigate to confirmation or handle errors
    };

    return (
        <div className="booking-form-container">
            <form onSubmit={handleSubmit} className="booking-form">
                <h2>Reservation Form</h2>
                <input type="text" name="name" placeholder="Enter your name" value={formData.name} onChange={handleChange} required />
                <input type="email" name="email" placeholder="Enter your email" value={formData.email} onChange={handleChange} required />
                <input type="tel" name="phone" placeholder="Enter your phone number" value={formData.phone} onChange={handleChange} required />
                <input type="date" name="reservationDate" value={formData.reservationDate} onChange={handleChange} required />
                <input type="time" name="time" value={formData.time} onChange={handleChange} required />
                <div className="form-buttons">
                    <button type="submit">Submit</button>
                    <button type="button" onClick={() => console.log('Canceling reservation')}>Cancel</button>
                </div>
            </form>
        </div>
    );
}

export default BookingForm;
