import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from '../axiosConfig';

function BookingForm() {
    const [bookingDetails, setBookingDetails] = useState({
        name: '',
        email: '',
        phone: '',
        date: '',
        time: ''
    });
    const navigate = useNavigate();

    const handleChange = (event) => {
        setBookingDetails({
            ...bookingDetails,
            [event.target.name]: event.target.value
        });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        const { name, email, phone, date, time } = bookingDetails;
        console.log('Attempting to submit:', { name, email, phone, date, time });  // Debug log
        try {
            const response = await axios.post('/api/reservations', {
                name,
                email,
                phone,
                date: `${date}T${time}:00`,  // Ensure the date and time are in ISO format
                status: 'Booked'
            });
            console.log('Reservation successful:', response.data);  // Debug log
            navigate('/confirmation');
        } catch (error) {
            console.error('Error making reservation:', error.response ? error.response.data : 'No response from server');
            alert('Failed to make a reservation. Please check the console for more details.');  // Display error to user
        }
    };

    return (
        <div className="booking-form">
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Name</label>
                    <input type="text" name="name" value={bookingDetails.name} onChange={handleChange} required />
                </div>
                <div className="form-group">
                    <label>Email</label>
                    <input type="email" name="email" value={bookingDetails.email} onChange={handleChange} required />
                </div>
                <div className="form-group">
                    <label>Phone Number</label>
                    <input type="tel" name="phone" value={bookingDetails.phone} onChange={handleChange} required />
                </div>
                <div className="form-group">
                    <label>Date</label>
                    <input type="date" name="date" value={bookingDetails.date} onChange={handleChange} required />
                </div>
                <div className="form-group">
                    <label>Time</label>
                    <input type="time" name="time" value={bookingDetails.time} onChange={handleChange} required />
                </div>
                <button type="submit" className="submit-button">Submit</button>
            </form>
        </div>
    );
}

export default BookingForm;
