import React, { useState } from 'react';
import axios from 'axios'; // Ensure axios is imported correctly
import { useNavigate } from 'react-router-dom'; // Import useNavigate for redirection

function BookingForm() {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        phone: '',
        reservationDate: '',
        time: ''
    });

    const navigate = useNavigate(); // Hook for navigation

    const handleChange = (event) => {
        setFormData({ ...formData, [event.target.name]: event.target.value });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        const { name, email, phone, reservationDate, time } = formData;
        const fullDateTime = `${reservationDate}T${time}:00`; // Combining date and time

        try {
            const response = await axios.post('/api/reservations', {
                name,
                email,
                phone,
                reservationDate: fullDateTime
            });
            console.log('Reservation successful:', response.data);
            navigate('/confirmation'); // Redirect to confirmation page after successful booking
        } catch (error) {
            console.error('Error making reservation:', error.response?.data || error.message);
            // Optionally, handle error state in UI here
        }
    };

    return (
        <div className="booking-form-container">
            <form onSubmit={handleSubmit} className="booking-form">
                <h2>Reservation Form</h2>
                <input type="text" name="name" placeholder="Enter your name" value={formData.name} onChange={handleChange} required />
                <input type="email" name="email" placeholder="Enter your email" value={formData.email} onChange={handleChange} required />
                <input type="tel" name="phone" placeholder="Enter your phone number" value={formData.phone} onChange={handleChange} required />
                <input type="date" name="reservationDate" value={formData.reservationDate} onChange={handleChange} required />
                <input type="time" name="time" value={formData.time} onChange={handleChange} required />
                <div className="form-buttons">
                    <button type="submit">Submit</button>
                    <button type="button" onClick={() => console.log('Canceling reservation')}>Cancel</button>
                </div>
            </form>
        </div>
    );
}

export default BookingForm;
