import React from 'react';
import { useNavigate } from 'react-router-dom';
function ConfirmationPage() {
    const navigate = useNavigate();

    // Retrieve reservation details from local storage
    const reservationDetails = JSON.parse(localStorage.getItem('reservationDetails')) || {};

    const handleViewAppointments = () => {
        navigate('/appointments');
    };

    return (
        <div className="confirmation-page">
            <h1>Reservation Created Successfully</h1>
            <p>Thank you for choosing our salon.</p>
            <div className="reservation-details">
                <p><strong>Name:</strong> {reservationDetails.name}</p>
                <p><strong>Email:</strong> {reservationDetails.email}</p>
                <p><strong>Phone:</strong> {reservationDetails.phone}</p>
                <p><strong>Date:</strong> {reservationDetails.date}</p>
                <p><strong>Time:</strong> {reservationDetails.time}</p>
            </div>
            <p>Appointment confirmed! Thank you for choosing our salon.</p>
            <button onClick={handleViewAppointments}>View My Appointments</button>
        </div>
    );
}

export default ConfirmationPage;
