import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css'; // Ensure this is correctly linked

const Navbar = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    useEffect(() => {
        const token = localStorage.getItem('token');
        setIsLoggedIn(!!token);
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token');
        setIsLoggedIn(false);
        window.location.reload(); // Or use a router method if using react-router
    };

    return (
        <div className='navbar'>
            <div onClick={() => window.location.href = '/'} className='navbar-logo'>BarberShop</div>
            <ul className='navbar-ul'>
                <li className='nav-items'><Link to="/" className='links'>Home</Link></li>
                <li className='nav-items'><Link to="/services" className='links'>Services</Link></li>
                <li className='nav-items'><Link to="/contact" className='links'>Contact Us</Link></li>
                <li className='nav-items'><Link to="/profile" className='links'>Profile</Link></li>
            </ul>
            {isLoggedIn ? (
                <div className='user-loggedIn'>
                    <p>Hello, User!</p>
                    <button onClick={handleLogout} className='logout-button'>Logout</button>
                </div>
            ) : (
                <div className='login-reg-links'>
                    <Link to='/login' className='login-link'>Login</Link>
                    <Link to='/register' className='register-link'>Register</Link>
                </div>
            )}
        </div>
    );
};

export default Navbar;
