import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css'; // Ensure the CSS path is correct

const Navbar = () => {
    const [name, setName] = useState('Guest');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const history = useHistory(); // Using useHistory hook for navigation

    useEffect(() => {
        // Check if the user is logged in and set state accordingly
        const token = localStorage.getItem('token');
        if (token) {
            setIsLoggedIn(true);
            setName('User'); // Update this to fetch the user's name if stored or decoded from the token
        }
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token');
        setIsLoggedIn(false);
        history.push('/'); // Redirects to homepage without reloading the page
    };

    return (
        <div className='navbar'>
            <h1 onClick={() => history.push('/')} className='navbar-logo'>
                BarberShop
            </h1>
            <div className='nav-menu'>
                <ul className='navbar-ul'>
                    <li className='nav-items'>
                        <Link to="/" className='links'>HOME</Link>
                    </li>
                    <li className='nav-items'>
                        <Link to="/services" className='links'>SLUŽBY</Link>
                    </li>
                    <li className='nav-items'>
                        <Link to="/contact" className='links'>KONTAKT</Link>
                    </li>
                    <li className='nav-items'>
                        <Link to="/profile" className='links'>PROFIL</Link>
                    </li>
                </ul>
                {isLoggedIn ? (
                    <div className='user-loggedIn'>
                        <p>Hello, {name}!</p>
                        <button onClick={handleLogout}>Logout</button>
                    </div>
                ) : (
                    <div className='login-reg-links'>
                        <Link to='/login' className='links'>Login</Link>
                        <span> </span>
                        <Link to='/register' className='links'>Register</Link>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Navbar;
