// Assuming your Footer component is correctly imported and set up
import React from 'react';
import { Link } from 'react-router-dom';
import './Home.css';
import Footer from './Footer';// Make sure your CSS file is linked

function Home() {
    const token = localStorage.getItem('token'); // Check for token to determine if logged in

    return (
        <div className="home">
            <div className="hero">
                <h1>Welcome to Alex's BarberShop</h1>
                <p>Your one-stop shop for all your grooming needs.</p>
                <div className="login-links">
                    {token ? <Link to="/services" className="link">Rezervace</Link> :
                        <Link to="/login" className="link">Login</Link>}
                </div>
            </div>
            <Footer />
        </div>
    );
}

export default Home;
