import React, { useState } from 'react';
import axios from '../axiosConfig';
import { useLocation, useNavigate } from 'react-router-dom';

function BookingForm() {
    const location = useLocation();
    const navigate = useNavigate();
    const queryParams = new URLSearchParams(location.search);
    const serviceId = queryParams.get('serviceId'); // Ensure this is the correct format
    const customerId = localStorage.getItem('customerId'); // Ensure this is being set on login

    // Temporary fix: Use dummy values for customerId and serviceId if not present
    const validCustomerId = customerId || '507f1f77bcf86cd799439011'; // Example valid ObjectId
    const validServiceId = serviceId || '507f1f77bcf86cd799439011'; // Example valid ObjectId

    const [bookingDetails, setBookingDetails] = useState({
        reservationDate: '',
        time: ''
    });

    const handleChange = (event) => {
        setBookingDetails({ ...bookingDetails, [event.target.name]: event.target.value });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        const fullDateTime = `${bookingDetails.reservationDate}T${bookingDetails.time}:00`;
        try {
            await axios.post('/api/reservations', {
                customerId: validCustomerId,
                serviceId: validServiceId,
                reservationDate: fullDateTime,
                status: 'Booked'
            });
            navigate('/confirmation');
        } catch (error) {
            console.error('Error making reservation:', error.response?.data || error.message);
        }
    };

    return (
        <div className="booking-form">
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Date</label>
                    <input type="date" name="reservationDate" className="input-field" onChange={handleChange} required />
                </div>
                <div className="form-group">
                    <label>Time</label>
                    <input type="time" name="time" className="input-field" onChange={handleChange} required />
                </div>
                <button type="submit">Book</button>
            </form>
        </div>
    );
}

export default BookingForm;
