import React from 'react';
import { useNavigate } from 'react-router-dom';
import './ConfirmationPage.css'; // Make sure to create and link the CSS file

function ConfirmationPage() {
    const navigate = useNavigate();

    // Dummy data, replace with actual data as needed
    const reservationDetails = {
        serviceName: 'Haircut',
        serviceDate: '2024-01-01',
        serviceTime: '10:00 AM',
        totalCost: 'XXX czk'
    };

    const handleViewAppointments = () => {
        navigate('/appointments'); // Adjust the route as necessary
    };

    return (
        <div className="confirmation-page">
            <h1>Reservation Created Successfully</h1>
            <p>Thank you for choosing our salon.</p>
            <div className="reservation-details">
                <p><strong>Service Name:</strong> {reservationDetails.serviceName}</p>
                <p><strong>Service Date:</strong> {reservationDetails.serviceDate}</p>
                <p><strong>Service Time:</strong> {reservationDetails.serviceTime}</p>
                <p><strong>Total Cost:</strong> {reservationDetails.totalCost}</p>
            </div>
            <p>Appointment confirmed! Thank you for choosing our salon.</p>
            <button onClick={handleViewAppointments}>View My Appointments</button>
        </div>
    );
}

export default ConfirmationPage;
